import React, { useEffect } from "react";
import grapesjs from "grapesjs";
import "grapesjs/dist/css/grapes.min.css";

export default function MailDesigner() {
  useEffect(() => {
    const editor = grapesjs.init({
      container: "#gjs",
      height: "100vh",
      fromElement: false,
      storageManager: false,
      panels: { defaults: [] }, // We'll manage panels manually
    });

    // Add sample blocks
    const blockManager = editor.BlockManager;
    blockManager.add("header", {
      label: "Header",
      content: "<h1>Header Text</h1>",
      category: "Basic",
    });
    blockManager.add("text", {
      label: "Text",
      content: "<p>Insert your text here</p>",
      category: "Basic",
    });
    blockManager.add("button", {
      label: "Button",
      content: '<button style="padding:10px 20px;">Click me</button>',
      category: "Basic",
    });

    // Render GrapesJS UI components into sidebar containers
    document.getElementById("blocks-container").appendChild(blockManager.render());
    document.getElementById("styles-container").appendChild(editor.StyleManager.render());
    document.getElementById("traits-container").appendChild(editor.TraitManager.render());
    document.getElementById("layers-container").appendChild(editor.LayerManager.render());

    return () => editor.destroy();
  }, []);

  return (
    <div style={{ display: "flex", height: "100vh", backgroundColor: "#f4f4f4" }}>
      {/* LEFT SIDEBAR */}
      <div
        id="left-sidebar"
        style={{
          width: "300px",
          backgroundColor: "#2f2f2f",
          color: "white",
          display: "flex",
          flexDirection: "column",
          overflowY: "auto",
          padding: "10px",
        }}
      >
        <h3 style={{ color: "#fff", marginBottom: "10px" }}>Mail Builder</h3>

        {/* Sidebar Tabs */}
        <div style={{ display: "flex", justifyContent: "space-around", marginBottom: "10px" }}>
          <button
            className="sidebar-btn"
            onClick={() => togglePanel("blocks-container")}
          >
            Blocks
          </button>
          <button
            className="sidebar-btn"
            onClick={() => togglePanel("layers-container")}
          >
            Layers
          </button>
          <button
            className="sidebar-btn"
            onClick={() => togglePanel("styles-container")}
          >
            Styles
          </button>
          <button
            className="sidebar-btn"
            onClick={() => togglePanel("traits-container")}
          >
            Traits
          </button>
        </div>

        {/* Panel Containers */}
        <div id="blocks-container" className="panel-section"></div>
        <div id="layers-container" className="panel-section" style={{ display: "none" }}></div>
        <div id="styles-container" className="panel-section" style={{ display: "none" }}></div>
        <div id="traits-container" className="panel-section" style={{ display: "none" }}></div>
      </div>

      {/* MAIN CANVAS AREA */}
      <div id="gjs" style={{ flex: 1, backgroundColor: "#fff" }}></div>
    </div>
  );
}

// Panel toggle helper
function togglePanel(id) {
  const panels = document.querySelectorAll(".panel-section");
  panels.forEach((p) => (p.style.display = "none"));
  document.getElementById(id).style.display = "block";
}
