import React, { useEffect, useState,useRef } from "react";
import grapesjs from "grapesjs";
import "grapesjs/dist/css/grapes.min.css"; // ‚úÖ only this is needed
import axios from "axios";
import "grapesjs-preset-newsletter";



const API_BASE = "http://43.230.201.125:60025/mails";

export default function MailDesigner() {
  const editorRef = useRef(null);
  const editorInstance = useRef(null);
  const [templates, setTemplates] = useState([]);
  const [selectedTemplate, setSelectedTemplate] = useState("");

  // Fetch templates from backend
  const fetchTemplates = async () => {
    try {
      const res = await axios.get(API_BASE);
      setTemplates(res.data);
    } catch (err) {
      console.error("Error loading templates:", err);
    }
  };

  // Initialize GrapesJS
  useEffect(() => {
    const editor = grapesjs.init({
      container: "#gjs",
      height: "88vh",
      width: "auto",
      fromElement: false,
      storageManager: false,
      plugins: ["gjs-preset-newsletter"],
      pluginsOpts: {
        "gjs-preset-newsletter": {
          modalTitleImport: "Import template",
          modalTitleExport: "Export template",
          showBlocksOnLoad: true,
        },
      },
      blockManager: {
        appendTo: "#blocks",
      },
      styleManager: {
        appendTo: "#styles",
      },
      panels: {
        defaults: [
          {
            id: "panel-top",
            el: ".panel__top",
          },
          {
            id: "panel-basic-actions",
            el: ".panel__basic-actions",
            buttons: [
              {
                id: "visibility",
                active: true, // active by default
                label: "Preview",
                command: "preview",
                togglable: false,
              },
            ],
          },
        ],
      },
    });

    editorInstance.current = editor;
    window.editor = editor; // for debugging in console
    fetchTemplates();

    return () => editor.destroy();
  }, []);

  // Save template
  const handleSave = async () => {
    const editor = editorInstance.current;
    if (!editor) return;

    const name = prompt("Enter template name:");
    if (!name) return;

    const html = editor.getHtml();
    const css = editor.getCss();
    const design = editor.getComponents();

    try {
      await axios.post(API_BASE, { name, html, css, design });
      alert("‚úÖ Template saved successfully!");
      fetchTemplates();
    } catch (err) {
      console.error(err);
      alert("‚ùå Failed to save template");
    }
  };

  // Load selected template
  const handleLoad = async (id) => {
    const template = templates.find((t) => t.id === id);
    if (!template) return;

    try {
      const editor = editorInstance.current;
      editor.setComponents(template.design);
      editor.setStyle(template.css);
      setSelectedTemplate(id);
    } catch (err) {
      console.error("Failed to load template:", err);
    }
  };

  // Export HTML
  const handleExport = () => {
    const editor = editorInstance.current;
    const html = editor.getHtml();
    const css = editor.getCss();
    const fullHtml = `
      <html>
        <head><style>${css}</style></head>
        <body>${html}</body>
      </html>
    `;
    const blob = new Blob([fullHtml], { type: "text/html" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "email_template.html";
    a.click();
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      {/* Top toolbar */}
      <div className="flex justify-between items-center bg-gray-900 text-white p-3 shadow-md">
        <h1 className="text-lg font-semibold">üìß Mail Template Builder</h1>
        <div className="flex items-center space-x-2">
          <select
            value={selectedTemplate}
            onChange={(e) => handleLoad(e.target.value)}
            className="text-black rounded-md px-2 py-1"
          >
            <option value="">Load Saved Template</option>
            {templates.map((t) => (
              <option key={t.id} value={t.id}>
                {t.name}
              </option>
            ))}
          </select>
          <button
            onClick={handleSave}
            className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded-md text-white"
          >
            üíæ Save
          </button>
          <button
            onClick={handleExport}
            className="bg-purple-600 hover:bg-purple-700 px-4 py-2 rounded-md text-white"
          >
            ‚¨áÔ∏è Export
          </button>
        </div>
      </div>

      {/* GrapesJS editor layout */}
      <div className="flex flex-grow">
        {/* Left blocks */}
        <div
          id="blocks"
          className="w-1/5 bg-white border-r border-gray-300 overflow-auto"
        ></div>

        {/* Main editor */}
        <div id="gjs" className="flex-1 bg-white shadow-inner"></div>

        {/* Right style panel */}
        <div
          id="styles"
          className="w-1/4 bg-white border-l border-gray-300 overflow-auto"
        ></div>
      </div>
    </div>
  );
}
