import React, { useState } from "react";
import "./AIModal.css";

export default function AIModal({ onClose, onRun }) {
  const [selectedAction, setSelectedAction] = useState("");
  const [prompt, setPrompt] = useState("");
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState("");
  const [error, setError] = useState("");

  const presets = {
    "Fix grammar": "Fix grammar and spelling issues in the following text:",
    "Make formal": "Rewrite the following email in a formal tone:",
    "Summarize": "Summarize the following email briefly:",
    "Make concise": "Rewrite this to be short, clear, and to the point:",
  };

  const handleActionSelect = (e) => {
    const value = e.target.value;
    setSelectedAction(value);
    setPrompt(presets[value] || "");
  };

  const runAI = async () => {
    if (!prompt.trim()) return alert("Please enter a prompt first!");
    setLoading(true);
    setResult("");
    setError("");

    try {
      const res = await fetch("https://puspeshd.pythonanywhere.com/send_prompt", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt }),
      });

      if (!res.ok) throw new Error("Network error");
      const data = await res.json();

      // Simulate polling or direct result
      if (data.result) setResult(data.result);
      else setResult("‚úÖ Prompt sent! Check backend for final result.");

    } catch (err) {
      setError("‚ùå Failed to get AI response. Try again.");
    }

    setLoading(false);
  };

  return (
    <div className="ai-modal-overlay">
      <div className="ai-modal-box">
        <div className="ai-modal-header">
          <h2>ü§ñ AI Assistant</h2>
          <button onClick={onClose}>‚úñ</button>
        </div>

        <div className="ai-modal-body">
          <label>Choose Action:</label>
          <select value={selectedAction} onChange={handleActionSelect}>
            <option value="">-- Select preset --</option>
            {Object.keys(presets).map((p) => (
              <option key={p} value={p}>{p}</option>
            ))}
          </select>

          <label>Edit Prompt:</label>
          <textarea
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Write or modify your AI prompt..."
          />

          {loading ? (
            <div className="ai-loader">‚öôÔ∏è Running AI... (max 1.5 min)</div>
          ) : (
            <button className="ai-run-btn" onClick={runAI}>
              Run AI
            </button>
          )}

          {result && (
            <>
              <label>AI Result:</label>
              <textarea readOnly value={result}></textarea>
              <div className="ai-actions">
                <button
                  onClick={() => {
                    navigator.clipboard.writeText(result);
                    alert("Copied!");
                  }}
                >
                  üìã Copy
                </button>
                <button onClick={() => setResult("")}>üîÅ Try Again</button>
              </div>
            </>
          )}

          {error && <p className="ai-error">{error}</p>}
        </div>
      </div>
    </div>
  );
}
