import React, { useEffect, useRef, useState } from "react";
import grapesjs from "grapesjs";
import "grapesjs/dist/css/grapes.min.css";
import "grapesjs-preset-newsletter";
import { Layers, Settings, Square, Type, Image as ImageIcon, Columns } from "lucide-react";

export default function MailDesigner() {
  const editorRef = useRef(null);
  const [activeTab, setActiveTab] = useState("blocks");

  useEffect(() => {
    const editor = grapesjs.init({
      container: "#gjs",
      height: "100vh",
      width: "100%",
      storageManager: false,
      plugins: ["gjs-preset-newsletter"],
      blockManager: { appendTo: "#blocks-panel" },
      layerManager: { appendTo: "#layers-panel" },
      styleManager: { appendTo: "#styles-panel" },
      traitManager: { appendTo: "#traits-panel" },
    });

    const bm = editor.BlockManager;
    bm.add("header", {
      label: "Header",
      content: `<table style="width:100%;background:#f3f3f3;text-align:center;padding:20px;"><tr><td><h1>Welcome!</h1></td></tr></table>`,
      category: "Basic",
    });
    bm.add("text", {
      label: "Text",
      content: `<div style="padding:10px;">Insert your text here...</div>`,
      category: "Basic",
    });
    bm.add("button", {
      label: "Button",
      content: `<div style="text-align:center;padding:10px;"><a href="#" style="background:#007bff;color:white;padding:10px 20px;border-radius:4px;text-decoration:none;">Click Me</a></div>`,
      category: "Basic",
    });
    bm.add("image", {
      label: "Image",
      content: `<img src="https://via.placeholder.com/600x200" style="width:100%;"/>`,
      category: "Media",
    });
    bm.add("two-columns", {
      label: "2 Columns",
      content: `
        <table style="width:100%;">
          <tr>
            <td style="width:50%;padding:10px;">Left Content</td>
            <td style="width:50%;padding:10px;">Right Content</td>
          </tr>
        </table>`,
      category: "Layout",
    });

    editorRef.current = editor;
    return () => editor.destroy();
  }, []);

  const leftTabs = [
    { id: "blocks", icon: <Square size={18} />, label: "Blocks" },
    { id: "layers", icon: <Layers size={18} />, label: "Layers" },
  ];

  const rightTabs = [
    { id: "styles", icon: <Settings size={18} />, label: "Styles" },
    { id: "traits", icon: <Type size={18} />, label: "Traits" },
  ];

  return (
    <div className="flex h-screen w-screen overflow-hidden bg-gray-900 text-white">
      {/* Left sidebar */}
      <div className="flex flex-col w-[280px] bg-[#2b2b2b] border-r border-gray-700">
        <div className="flex justify-around py-2 bg-[#1e1e1e]">
          {leftTabs.map((tab) => (
            <button
              key={tab.id}
              title={tab.label}
              onClick={() => setActiveTab(tab.id)}
              className={`p-2 rounded ${
                activeTab === tab.id
                  ? "bg-indigo-600 text-white"
                  : "text-gray-400 hover:text-white"
              }`}
            >
              {tab.icon}
            </button>
          ))}
        </div>
        <div className="flex-1 overflow-y-auto p-2">
          {activeTab === "blocks" && <div id="blocks-panel"></div>}
          {activeTab === "layers" && <div id="layers-panel"></div>}
        </div>
      </div>

      {/* Canvas */}
      <div id="gjs" className="flex-1 bg-white" />

      {/* Right sidebar */}
      <div className="flex flex-col w-[280px] bg-[#2b2b2b] border-l border-gray-700">
        <div className="flex justify-around py-2 bg-[#1e1e1e]">
          {rightTabs.map((tab) => (
            <button
              key={tab.id}
              title={tab.label}
              onClick={() => setActiveTab(tab.id)}
              className={`p-2 rounded ${
                activeTab === tab.id
                  ? "bg-indigo-600 text-white"
                  : "text-gray-400 hover:text-white"
              }`}
            >
              {tab.icon}
            </button>
          ))}
        </div>
        <div className="flex-1 overflow-y-auto p-2">
          {activeTab === "styles" && <div id="styles-panel"></div>}
          {activeTab === "traits" && <div id="traits-panel"></div>}
        </div>
      </div>
    </div>
  );
}
