import React, { useEffect, useState, useRef } from "react";
import grapesjs from "grapesjs";
import "grapesjs/dist/css/grapes.min.css";
import "grapesjs-preset-newsletter";
import axios from "axios";
import {
  Layers,
  Settings,
  Square,
  Type,
  Image as ImageIcon,
  Columns,
  Save,
  Download,
  ChevronDown,
  ChevronRight,
} from "lucide-react";

const API_BASE = "http://127.0.0.1:5000/mails";

export default function MailDesigner() {
  const editorInstance = useRef(null);
  const [templates, setTemplates] = useState([]);
  const [selectedTemplate, setSelectedTemplate] = useState("");
  const [activeTab, setActiveTab] = useState("blocks");
  const [openCategories, setOpenCategories] = useState({
    Basic: true,
    Media: true,
    Layout: true,
  });

  // Fetch templates
  const fetchTemplates = async () => {
    try {
      const res = await axios.get(API_BASE);
      setTemplates(res.data);
    } catch (err) {
      console.error("Error loading templates:", err);
    }
  };

  // Initialize GrapesJS
  useEffect(() => {
    const editor = grapesjs.init({
      container: "#gjs",
      height: "88vh",
      storageManager: false,
      plugins: ["gjs-preset-newsletter"],
      blockManager: { appendTo: "#blocks-content" },
      layerManager: { appendTo: "#layers-panel" },
      styleManager: { appendTo: "#styles-panel" },
      traitManager: { appendTo: "#traits-panel" },
      deviceManager: { appendTo: "#styles-panel" }, // keep gray style area consistent
    });

    // Custom Blocks
    const bm = editor.BlockManager;
    bm.add("header", {
      label: `<div class="flex items-center gap-1"><Square size={14}/> Header</div>`,
      content: `<table style="width:100%;background:#f3f3f3;text-align:center;padding:20px;"><tr><td><h1 style="margin:0;">Welcome!</h1></td></tr></table>`,
      category: "Basic",
    });
    bm.add("text", {
      label: `<div class="flex items-center gap-1"><Type size={14}/> Text</div>`,
      content: `<div style="padding:10px;">Insert your text here...</div>`,
      category: "Basic",
    });
    bm.add("button", {
      label: `<div class="flex items-center gap-1"><Square size={14}/> Button</div>`,
      content: `<div style="text-align:center;padding:10px;"><a href="#" style="background:#007bff;color:white;padding:10px 20px;border-radius:4px;text-decoration:none;">Click Me</a></div>`,
      category: "Basic",
    });
    bm.add("image", {
      label: `<div class="flex items-center gap-1"><ImageIcon size={14}/> Image</div>`,
      content: `<img src="https://via.placeholder.com/600x200" style="width:100%;"/>`,
      category: "Media",
    });
    bm.add("two-columns", {
      label: `<div class="flex items-center gap-1"><Columns size={14}/> Two Columns</div>`,
      content: `
        <table style="width:100%;">
          <tr>
            <td style="width:50%;padding:10px;">Left Content</td>
            <td style="width:50%;padding:10px;">Right Content</td>
          </tr>
        </table>
      `,
      category: "Layout",
    });

    // Disable default toggles & collapse all categories
    editor.on("load", () => {
      const categories = editor.BlockManager.getCategories();
      categories.each((cat) => cat.set("open", false));
    });

    editorInstance.current = editor;
    fetchTemplates();

    return () => editor.destroy();
  }, []);

  // Save template
  const handleSave = async () => {
    const editor = editorInstance.current;
    if (!editor) return;
    const name = prompt("Enter template name:");
    if (!name) return;

    const html = editor.getHtml();
    const css = editor.getCss();
    const design = editor.getComponents();

    try {
      await axios.post(API_BASE, { name, html, css, design });
      alert("âœ… Template saved successfully!");
      fetchTemplates();
    } catch (err) {
      console.error(err);
      alert("âŒ Failed to save template");
    }
  };

  // Load template
  const handleLoad = (id) => {
    const template = templates.find((t) => t.id === id);
    if (!template) return;
    const editor = editorInstance.current;
    editor.setComponents(template.design);
    editor.setStyle(template.css);
    setSelectedTemplate(id);
  };

  // Export HTML
  const handleExport = () => {
    const editor = editorInstance.current;
    const html = editor.getHtml();
    const css = editor.getCss();
    const fullHtml = `
      <html>
        <head><style>${css}</style></head>
        <body>${html}</body>
      </html>`;
    const blob = new Blob([fullHtml], { type: "text/html" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "email_template.html";
    a.click();
  };

  // Toggle category
  const toggleCategory = (cat) =>
    setOpenCategories((prev) => ({ ...prev, [cat]: !prev[cat] }));

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      {/* Top Toolbar */}
      <div className="flex justify-between items-center bg-indigo-700 text-white px-6 py-3 shadow-md">
        <h1 className="text-xl font-semibold tracking-wide">
          ðŸ“§ Mail Template Builder
        </h1>
        <div className="flex items-center space-x-3">
          <select
            value={selectedTemplate}
            onChange={(e) => handleLoad(e.target.value)}
            className="text-black rounded-md px-2 py-1 bg-white"
          >
            <option value="">Load Template</option>
            {templates.map((t) => (
              <option key={t.id} value={t.id}>
                {t.name}
              </option>
            ))}
          </select>

          <button
            onClick={handleSave}
            className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded-md flex items-center gap-1"
          >
            <Save size={16} /> Save
          </button>

          <button
            onClick={handleExport}
            className="bg-purple-600 hover:bg-purple-700 px-4 py-2 rounded-md flex items-center gap-1"
          >
            <Download size={16} /> Export
          </button>
        </div>
      </div>

      {/* Main Layout */}
      <div className="flex flex-grow overflow-hidden">
        {/* Left Sidebar */}
        <div className="w-72 bg-[#2f2f2f] text-white flex flex-col">
          {/* Tabs */}
          <div className="flex justify-between border-b border-gray-700">
            {[
              { id: "blocks", icon: <Square size={18} />, label: "Blocks" },
              { id: "layers", icon: <Layers size={18} />, label: "Layers" },
              { id: "styles", icon: <Settings size={18} />, label: "Styles" },
              { id: "traits", icon: <Type size={18} />, label: "Traits" },
            ].map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex-1 py-2 text-sm font-medium flex items-center justify-center gap-1 border-b-2 ${
                  activeTab === tab.id
                    ? "border-indigo-500 text-indigo-400 bg-[#3f3f3f]"
                    : "border-transparent text-gray-400 hover:bg-[#444]"
                }`}
              >
                {tab.icon}
                {tab.label}
              </button>
            ))}
          </div>

          {/* Panels */}
          <div className="flex-1 overflow-y-auto p-2">
            {/* Blocks */}
            {activeTab === "blocks" && (
              <div id="blocks-content" className="gjs-custom-panel"></div>
            )}

            {/* Layers */}
            <div id="layers-panel" className={activeTab === "layers" ? "" : "hidden"} />

            {/* Styles */}
            <div id="styles-panel" className={activeTab === "styles" ? "" : "hidden"} />

            {/* Traits */}
            <div id="traits-panel" className={activeTab === "traits" ? "" : "hidden"} />
          </div>
        </div>

        {/* GrapesJS Canvas */}
        <div id="gjs" className="flex-1 bg-white shadow-inner" />
      </div>
    </div>
  );
}
