// src/pages/MailDesigner.jsx
import React, { useRef, useEffect, useState } from "react";
import EmailEditor from "react-email-editor";
import "./MailDesigner.css";

export default function MailDesigner() {
  const emailEditorRef = useRef(null);
  const [templates, setTemplates] = useState([]);

  // Load existing templates from backend
  useEffect(() => {
    fetch("http://localhost:5000/api/templates")
      .then((res) => res.json())
      .then((data) => setTemplates(data))
      .catch((err) => console.error("Error loading templates:", err));
  }, []);

  const exportHtml = () => {
    emailEditorRef.current.editor.exportHtml((data) => {
      const { design, html } = data;

      fetch("http://localhost:5000/api/save-template", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ design, html, name: "New Template" }),
      })
        .then((res) => res.json())
        .then((result) => alert("Template saved successfully!"))
        .catch((err) => console.error("Error saving template:", err));
    });
  };

  const loadTemplate = (design) => {
    emailEditorRef.current.editor.loadDesign(design);
  };

  return (
    <div className="mail-designer">
      <div className="designer-header">
        <h2>Email Template Designer</h2>
        <div>
          <button onClick={exportHtml} className="btn-save">ðŸ’¾ Save</button>
        </div>
      </div>

      <div className="template-list">
        {templates.map((t, i) => (
          <button key={i} onClick={() => loadTemplate(t.design)} className="template-btn">
            {t.name}
          </button>
        ))}
      </div>

      <div className="editor-container">
        <EmailEditor ref={emailEditorRef} minHeight="85vh" />
      </div>
    </div>
  );
}
