import React, { useEffect, useRef, useState } from "react";
import EmailEditor from "react-email-editor";
import axios from "axios";

const API_BASE = "http://127.0.0.1:5000/mails";

export default function MailDesigner() {
  const emailEditorRef = useRef(null);
  const [templates, setTemplates] = useState([]);
  const [selectedTemplate, setSelectedTemplate] = useState("");

  // Fetch saved templates from backend
  const fetchTemplates = async () => {
    try {
      const res = await axios.get(API_BASE);
      setTemplates(res.data);
    } catch (err) {
      console.error("Error loading templates:", err);
    }
  };

  useEffect(() => {
    fetchTemplates();
  }, []);

  // Save current template with user-entered name
  const handleSave = () => {
    emailEditorRef.current.editor.exportHtml(async ({ design, html }) => {
      const name = prompt("Enter template name:");
      if (!name) return;

      try {
        await axios.post(API_BASE, { name, html, design });
        alert("âœ… Template saved successfully!");
        fetchTemplates();
      } catch (err) {
        console.error(err);
        alert("âŒ Failed to save template");
      }
    });
  };

  // Load template by id from API data
  const handleLoad = (id) => {
    const template = templates.find((t) => t.id === id);
    if (!template) return;
    emailEditorRef.current.editor.loadDesign(template.design);
    setSelectedTemplate(id);
  };

  // Export current design to downloadable HTML file
  const handleExport = () => {
    emailEditorRef.current.editor.exportHtml(({ html }) => {
      const fullHtml = `<html><head></head><body>${html}</body></html>`;
      const blob = new Blob([fullHtml], { type: "text/html" });
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = "email_template.html";
      a.click();
    });
  };

  // Editor options with full blocks and custom blocks declared here
  const editorOptions = {
    appearance: {
      theme: "light",
    },
    tools: {
      button: { enabled: true },
      text: { enabled: true },
      image: { enabled: true },
      social: { enabled: true },
      menu: { enabled: true },
      divider: { enabled: true },
      video: { enabled: true },
      countdown: { enabled: true },
      form: { enabled: true },
      html: { enabled: true },
      spacer: { enabled: true },

      // Custom block example
      custom_header: {
        enabled: true,
        label: "Custom Header",
        icon:
          "https://cdn.iconscout.com/icon/free/png-256/header-1781135-1513505.png",
        attributes: {
          html: `<div style="background:#e3f2fd;padding:20px;text-align:center;">
                    <h2 style="margin:0;color:#1976d2;">Welcome to Our Newsletter!</h2>
                </div>`,
        },
        category: "Custom",
      },
    },
    displayMode: "email",
    features: {
      preheaderText: true,
      undoRedo: true,
      importExport: true,
      responsive: true,
      fontFamily: true,
      mergeTags: true,
    },
    safeHtml: true,
    exportOpts: {
      sanitizeHtml: true,
    },
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      {/* Header Toolbar */}
      <div className="flex justify-between items-center bg-indigo-700 text-white px-6 py-3 shadow-md">
        <h1 className="text-xl font-semibold tracking-wide">ðŸ“§ Advanced Mail Template Builder</h1>
        <div className="flex items-center space-x-3">
          <select
            value={selectedTemplate}
            onChange={(e) => handleLoad(e.target.value)}
            className="text-black rounded-md px-3 py-1"
          >
            <option value="">Load Template</option>
            {templates.map((t) => (
              <option key={t.id} value={t.id}>
                {t.name}
              </option>
            ))}
          </select>
          <button
            onClick={handleSave}
            className="bg-green-600 hover:bg-green-700 px-4 py-2 rounded-md font-semibold"
          >
            Save Template
          </button>
          <button
            onClick={handleExport}
            className="bg-purple-600 hover:bg-purple-700 px-4 py-2 rounded-md font-semibold"
          >
            Export HTML
          </button>
        </div>
      </div>

      {/* Editor Canvas */}
      <div className="flex-1 p-4 bg-white">
        <EmailEditor ref={emailEditorRef} options={editorOptions} height="85vh" />
      </div>
    </div>
  );
}
