import React, { useState, useEffect } from "react";
import toast, { Toaster } from "react-hot-toast";
import { FiMail, FiTrash2, FiEye, FiPlus, FiX } from "react-icons/fi";

const API_BASE_URL = "http://43.230.201.125:60025/";

function Modal({ children, onClose }) {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex justify-center items-start pt-20 z-50" onClick={onClose}>
      <div
        className="bg-white rounded-lg shadow-lg max-w-4xl w-full max-h-[80vh] overflow-auto p-6 relative"
        onClick={(e) => e.stopPropagation()}
      >
        <button
          className="absolute top-4 right-4 text-gray-500 hover:text-gray-900 transition"
          onClick={onClose}
          aria-label="Close modal"
        >
          <FiX size={24} />
        </button>
        {children}
      </div>
    </div>
  );
}

function CollapsibleSection({ title, children, defaultOpen = true }) {
  const [open, setOpen] = useState(defaultOpen);

  return (
    <div className="my-4 border rounded-lg shadow-sm">
      <button
        className="w-full px-5 py-3 text-left font-semibold text-lg flex justify-between items-center bg-gray-100 rounded-t-lg focus:outline-none"
        onClick={() => setOpen(!open)}
      >
        {title}
        <span className="text-xl font-bold select-none">{open ? "−" : "+"}</span>
      </button>
      {open && <div className="p-5 bg-white border-t">{children}</div>}
    </div>
  );
}

export default function Dashboard() {
  const [containers, setContainers] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [logs, setLogs] = useState("");
  const [loading, setLoading] = useState(false);
  const [sendLoading, setSendLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState("");
  const [stats, setStats] = useState(null);
  const [logFilter, setLogFilter] = useState("");
  const [emailLogs, setEmailLogs] = useState([]);
  const [emailsLoading, setEmailsLoading] = useState(false);
  const [emailsError, setEmailsError] = useState("");
  const [viewingMail, setViewingMail] = useState(null);

  const notifyError = (msg) => toast.error(msg || "An error occurred");
  const notifySuccess = (msg) => toast.success(msg);

  const fetchContainers = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}containers`);
      if (!res.ok) throw new Error(await res.text());
      const data = await res.json();
      setContainers(data);
      setErrorMsg("");
    } catch (err) {
      setErrorMsg(err.message || "Unknown error");
      notifyError(err.message);
    }
  };

  const fetchStats = async (id) => {
    try {
      const res = await fetch(`${API_BASE_URL}containers/stats/${id}`);
      if (!res.ok) throw new Error(await res.text());
      const data = await res.json();
      setStats(data);
    } catch (err) {
      setErrorMsg(err.message || "Unknown error");
      notifyError(err.message);
    }
  };

  const fetchEmailLogs = async (containerId) => {
    setEmailsLoading(true);
    try {
      const res = await fetch(`${API_BASE_URL}containers/maildata/${containerId}`);
      if (!res.ok) throw new Error(await res.text());
      const data = await res.json();
      setEmailLogs(data);
      setEmailsError("");
    } catch (err) {
      setEmailsError(err.message || "Failed to load email logs");
      setEmailLogs([]);
      notifyError(err.message);
    } finally {
      setEmailsLoading(false);
    }
  };

  const createContainer = async () => {
    setLoading(true);
    try {
      const res = await fetch(`${API_BASE_URL}containers/create`, { method: "POST" });
      if (!res.ok) throw new Error(await res.text());
      await fetchContainers();
      notifySuccess("Container created successfully");
      setErrorMsg("");
    } catch (err) {
      setErrorMsg(err.message || "Unknown error");
      notifyError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const deleteContainer = async (id) => {
    setLoading(true);
    try {
      const res = await fetch(`${API_BASE_URL}containers/delete/${id}`, { method: "DELETE" });
      if (!res.ok) throw new Error(await res.text());
      if (id === selectedId) {
        setSelectedId(null);
        setLogs("");
        setStats(null);
        setEmailLogs([]);
      }
      await fetchContainers();
      notifySuccess("Container deleted successfully");
      setErrorMsg("");
    } catch (err) {
      setErrorMsg(err.message || "Unknown error");
      notifyError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const viewLogs = async (id, filter = "") => {
    setSelectedId(id);
    setLogFilter(filter);
    try {
      const url = filter
        ? `${API_BASE_URL}containers/logs/${id}?filter=${filter}`
        : `${API_BASE_URL}containers/logs/${id}`;
      const res = await fetch(url);
      if (!res.ok) throw new Error(await res.text());
      const data = await res.json();
      setLogs(data.logs);
      setErrorMsg("");
    } catch (err) {
      setErrorMsg(err.message || "Unknown error");
      notifyError(err.message);
      setLogs("");
    }
    fetchStats(id);
    fetchEmailLogs(id);
  };

  const sendEmails = async (id) => {
    setSendLoading(true);
    try {
      const res = await fetch(`${API_BASE_URL}containers/send-emails/${id}`, { method: "POST" });
      if (!res.ok) throw new Error(await res.text());
      const data = await res.json();
      notifySuccess(`Emails Sent: ${data.sent_in_batch || "N/A"}, Total: ${data.total_sent || "N/A"}`);
      await fetchStats(id);
      await viewLogs(id, logFilter);
      setErrorMsg("");
    } catch (err) {
      setErrorMsg(err.message || "Unknown error");
      notifyError(err.message);
    } finally {
      setSendLoading(false);
    }
  };

  useEffect(() => {
    fetchContainers();
  }, []);

  const closeModal = () => {
    setSelectedId(null);
    setLogs("");
    setStats(null);
    setEmailLogs([]);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6 font-sans text-gray-900">
      <Toaster position="top-right" toastOptions={{ duration: 4000 }} />
      <header className="mb-6">
        <h1 className="text-3xl font-bold border-b pb-2 border-gray-300 w-max">SMTP Docker Dashboard</h1>
      </header>

      <div className="mb-6">
        <button
          onClick={createContainer}
          disabled={loading || sendLoading}
          className="bg-blue-600 disabled:bg-blue-300 text-white px-5 py-3 rounded-lg shadow-md hover:bg-blue-700 transition flex items-center gap-2"
        >
          <FiPlus size={20} />
          {loading ? "Creating..." : "Create New SMTP Container"}
        </button>
      </div>

      {errorMsg && (
        <div className="mb-4 p-3 rounded bg-red-100 text-red-700 font-semibold shadow-sm">
          {errorMsg}
        </div>
      )}

      <section className="overflow-x-auto rounded-lg shadow-lg bg-white">
        <table className="min-w-full text-left border-collapse">
          <thead className="bg-blue-600 text-white font-semibold">
            <tr>
              <th className="px-6 py-3 rounded-tl-lg">Name</th>
              <th className="px-6 py-3">Status</th>
              <th className="px-6 py-3">Base Email</th>
              <th className="px-6 py-3">User Count</th>
              <th className="px-6 py-3">Uptime (sec)</th>
              <th className="px-6 py-3">Emails Sent</th>
              <th className="px-6 py-3 rounded-tr-lg">Actions</th>
            </tr>
          </thead>
          <tbody>
            {containers.length === 0 ? (
              <tr>
                <td colSpan="7" className="text-center p-6 text-gray-500">
                  No containers found.
                </td>
              </tr>
            ) : (
              containers.map((c) => (
                <tr key={c.id} className="bg-gray-50 odd:bg-white hover:bg-gray-100">
                  <td className="px-6 py-4 font-semibold">{c.name}</td>
                  <td className="px-6 py-4">{c.status}</td>
                  <td className="px-6 py-4 truncate max-w-xs">{c.base_email}</td>
                  <td className="px-6 py-4">{c.user_count}</td>
                  <td className="px-6 py-4">{c.uptime_seconds}</td>
                  <td className="px-6 py-4">{c.emails_sent}</td>
                  <td className="px-6 py-4 flex gap-2">
                    <button
                      onClick={() => viewLogs(c.id)}
                      className="flex items-center gap-1 bg-yellow-400 hover:bg-yellow-500 text-black px-3 py-1 rounded shadow"
                      title="View Logs"
                    >
                      <FiEye />
                    </button>
                    <button
                      onClick={() => sendEmails(c.id)}
                      disabled={sendLoading || loading}
                      className="flex items-center gap-1 bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded shadow"
                      title="Send Emails"
                    >
                      <FiMail />
                    </button>
                    <button
                      onClick={() => deleteContainer(c.id)}
                      disabled={loading || sendLoading}
                      className="flex items-center gap-1 bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded shadow"
                      title="Delete Container"
                    >
                      <FiTrash2 />
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </section>

      {selectedId && (
        <Modal onClose={closeModal}>
          {stats && (
            <CollapsibleSection title={`Container Stats for ${selectedId}`}>
              <p><strong>CPU:</strong> {stats.cpu_percent.toFixed(2)}%</p>
              <p>
                <strong>Memory:</strong> {(stats.memory_usage / 1024 / 1024).toFixed(2)} MiB /{" "}
                {(stats.memory_limit / 1024 / 1024).toFixed(2)} MiB ({stats.memory_percent.toFixed(2)}%)
              </p>
              <p><strong>Uptime:</strong> {stats.uptime_seconds} seconds</p>
              <p><strong>Emails Sent:</strong> {stats.emails_sent}</p>
            </CollapsibleSection>
          )}

          <CollapsibleSection title="Logs">
            <pre className="bg-gray-900 text-green-400 p-4 rounded max-h-72 overflow-auto whitespace-pre-wrap">
              {logs || "No logs available."}
            </pre>
          </CollapsibleSection>

          <CollapsibleSection title="Email Logs">
            {emailsLoading && <p>Loading emails...</p>}
            {emailsError && <p className="text-red-600">{emailsError}</p>}
            {!emailsLoading && emailLogs.length === 0 && <p>No emails logged yet.</p>}

            {!emailsLoading && emailLogs.length > 0 && (
              <div className="overflow-x-auto max-h-80">
                <table className="min-w-full border-collapse text-left">
                  <thead className="bg-gray-200 sticky top-0 z-10">
                    <tr>
                      <th className="px-4 py-2">Recipient</th>
                      <th className="px-4 py-2">Subject</th>
                      <th className="px-4 py-2">Status</th>
                      <th className="px-4 py-2">Timestamp</th>
                      <th className="px-4 py-2 max-w-xs">Snippet</th>
                      <th className="px-4 py-2">View</th>
                    </tr>
                  </thead>
                  <tbody>
                    {emailLogs.map((mail, idx) => (
                      <tr
                        key={idx}
                        className="odd:bg-white even:bg-gray-100 hover:bg-yellow-50 cursor-pointer"
                      >
                        <td className="px-4 py-2 max-w-xs truncate">{mail.to}</td>
                        <td className="px-4 py-2 max-w-xs truncate">{mail.subject}</td>
                        <td className="px-4 py-2">{mail.status}</td>
                        <td className="px-4 py-2">{new Date(mail.timestamp * 1000).toLocaleString()}</td>
                        <td
                          className="px-4 py-2 max-w-sm truncate"
                          dangerouslySetInnerHTML={{ __html: mail.body_snippet }}
                        />
                        <td className="px-4 py-2 text-center">
                          <button
                            className="text-blue-600 hover:text-blue-800"
                            onClick={() => setViewingMail(mail)}
                            aria-label="View full mail"
                          >
                            ✉️
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
            {viewingMail && (
              <Modal onClose={() => setViewingMail(null)}>
                <h3 className="text-xl font-bold mb-2">{viewingMail.subject}</h3>
                <p>
                  <strong>To:</strong> {viewingMail.to}
                </p>
                <p>
                  <strong>Status:</strong> {viewingMail.status}
                </p>
                <hr className="my-3" />
                <div
                  className="border p-4 rounded bg-white max-h-[70vh] overflow-auto"
                  dangerouslySetInnerHTML={{
                    __html: viewingMail.body_html || viewingMail.body_snippet,
                  }}
                />
              </Modal>
            )}
          </CollapsibleSection>
        </Modal>
      )}
    </div>
  );
}
