import React, { useEffect, useRef } from "react";
import grapesjs from "grapesjs";
import "grapesjs/dist/css/grapes.min.css";
import "grapesjs-preset-newsletter/dist/grapesjs-preset-newsletter.css";

export default function MailDesigner() {
  const editorRef = useRef(null);

  useEffect(() => {
    if (!editorRef.current) return;

    const editor = grapesjs.init({
      container: editorRef.current,
      height: "90vh",
      width: "auto",
      fromElement: false,
      storageManager: false,
      plugins: ["gjs-preset-newsletter"],
      pluginsOpts: {
        "gjs-preset-newsletter": {
          modalTitleImport: "Import template",
          modalTitleExport: "Export template",
          showBlocksOnLoad: true,
        },
      },
      blockManager: {
        appendTo: "#blocks",
      },
      styleManager: { clearProperties: 1 },
      panels: { defaults: [] },
    });

    // Expose globally if you want debug access in console
    window.editor = editor;

    return () => editor.destroy();
  }, []);

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Left panel for blocks */}
      <div id="blocks" className="w-1/5 bg-white border-r p-2 overflow-auto"></div>

      {/* Main editor area */}
      <div ref={editorRef} className="flex-1 bg-white shadow-lg"></div>
    </div>
  );
}
