import React, { useEffect, useState, useRef } from "react";
import EmailEditor from "react-email-editor";
import axios from "axios";

const API_BASE = "http://127.0.0.1:5000/mails";

export default function MailDesigner() {
  const emailEditorRef = useRef(null);
  const [templates, setTemplates] = useState([]);
  const [selectedTemplate, setSelectedTemplate] = useState("");
  const [activeTab, setActiveTab] = useState("blocks");

  const fetchTemplates = async () => {
    try {
      const res = await axios.get(API_BASE);
      setTemplates(res.data);
    } catch (err) {
      console.error("Error loading templates:", err);
    }
  };

  useEffect(() => {
    fetchTemplates();
  }, []);

  // Save current design with template name
  const handleSave = () => {
    emailEditorRef.current.editor.exportHtml(async ({ design, html }) => {
      const name = prompt("Enter template name:");
      if (!name) return;

      try {
        await axios.post(API_BASE, { name, html, design });
        alert("âœ… Template saved successfully!");
        fetchTemplates();
      } catch (err) {
        console.error(err);
        alert("âŒ Failed to save template");
      }
    });
  };

  // Load a template design into the editor
  const handleLoad = (id) => {
    const template = templates.find((t) => t.id === id);
    if (!template) return;
    emailEditorRef.current.editor.loadDesign(template.design);
    setSelectedTemplate(id);
  };

  // Export HTML file
  const handleExport = () => {
    emailEditorRef.current.editor.exportHtml(({ design, html }) => {
      const fullHtml = `
        <html><head></head><body>${html}</body></html>
      `;
      const blob = new Blob([fullHtml], { type: "text/html" });
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = "email_template.html";
      a.click();
    });
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      {/* Top Toolbar */}
      <div className="flex justify-between items-center bg-indigo-700 text-white px-6 py-3 shadow-md">
        <h1 className="text-xl font-semibold tracking-wide">ðŸ“§ Mail Template Builder</h1>
        <div className="flex items-center space-x-3">
          <select
            value={selectedTemplate}
            onChange={(e) => handleLoad(e.target.value)}
            className="text-black rounded-md px-2 py-1 bg-white"
          >
            <option value="">Load Template</option>
            {templates.map((t) => (
              <option key={t.id} value={t.id}>
                {t.name}
              </option>
            ))}
          </select>
          <button
            onClick={handleSave}
            className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded-md flex items-center gap-1"
          >
            Save
          </button>
          <button
            onClick={handleExport}
            className="bg-purple-600 hover:bg-purple-700 px-4 py-2 rounded-md flex items-center gap-1"
          >
            Export
          </button>
        </div>
      </div>

      {/* Email Editor Canvas */}
      <div className="flex-1">
        <EmailEditor ref={emailEditorRef} />
      </div>
    </div>
  );
}
