import React, { useEffect, useState } from "react";

const API_BASE_URL = "http://127.0.0.1:5000/";

export default function MailDesigner() {
  const [templates, setTemplates] = useState([]);
  const [newTemplate, setNewTemplate] = useState("");
  const [loading, setLoading] = useState(false);

  const fetchTemplates = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}templates`);
      if (!res.ok) throw new Error(await res.text());
      const data = await res.json();
      setTemplates(data);
    } catch (err) {
      console.error("Failed to load templates:", err);
    }
  };

  const saveTemplate = async () => {
    if (!newTemplate.trim()) return alert("Template cannot be empty.");
    setLoading(true);
    try {
      const res = await fetch(`${API_BASE_URL}templates`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ content: newTemplate }),
      });
      if (!res.ok) throw new Error(await res.text());
      await fetchTemplates();
      setNewTemplate("");
      alert("Template saved!");
    } catch (err) {
      console.error("Failed to save:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTemplates();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h1>Mail Designer</h1>

      <textarea
        rows="10"
        style={{ width: "100%", marginBottom: "10px" }}
        placeholder="Write your email HTML here..."
        value={newTemplate}
        onChange={(e) => setNewTemplate(e.target.value)}
      ></textarea>

      <button
        onClick={saveTemplate}
        disabled={loading}
        style={{
          padding: "10px 15px",
          backgroundColor: "#4CAF50",
          color: "white",
          border: "none",
          borderRadius: "5px",
        }}
      >
        {loading ? "Saving..." : "Save Template"}
      </button>

      <h2 style={{ marginTop: "30px" }}>Saved Templates</h2>
      {templates.length === 0 ? (
        <p>No templates found.</p>
      ) : (
        <ul>
          {templates.map((t, idx) => (
            <li key={idx}>
              <pre>{t.content}</pre>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
