import React, { useState, useEffect } from 'react';

function App() {
  const [containers, setContainers] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [logs, setLogs] = useState('');
  const [loading, setLoading] = useState(false);

  // Fetch all containers
  const fetchContainers = async () => {
    const res = await fetch('/containers');
    const data = await res.json();
    setContainers(data);
  };

  // Create a new container
  const createContainer = async () => {
    setLoading(true);
    await fetch('/containers/create', { method: 'POST' });
    setLoading(false);
    fetchContainers();
  };

  // Delete container
  const deleteContainer = async (id) => {
    setLoading(true);
    await fetch(`/containers/delete/${id}`, { method: 'DELETE' });
    setLoading(false);
    fetchContainers();
  };

  // View container logs
  const viewLogs = async (id) => {
    setSelectedId(id);
    const res = await fetch(`/containers/logs/${id}`);
    const data = await res.json();
    setLogs(data.logs);
  };

  useEffect(() => {
    fetchContainers();
  }, []);

  return (
    <div style={{ maxWidth: 800, margin: 'auto', padding: 32 }}>
      <h2>SMTP Docker Dashboard</h2>
      <button onClick={createContainer} disabled={loading}>
        {loading ? 'Creating...' : 'Create New SMTP Container'}
      </button>
      <h3>Active Containers</h3>
      <table border="1" cellPadding="6" style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {containers.map(cont => (
            <tr key={cont.id}>
              <td>{cont.name}</td>
              <td>{cont.status}</td>
              <td>
                <button onClick={() => viewLogs(cont.id)}>
                  View Logs
                </button>
                <button onClick={() => deleteContainer(cont.id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3>Container Logs</h3>
      {selectedId ? (
        <div style={{ whiteSpace: 'pre', background: '#eee', padding: 10, marginTop: 20 }}>
          {logs || "No logs found."}
        </div>
      ) : (
        <p>Select a container to view logs.</p>
      )}
    </div>
  );
}

export default App;
