import React, { useState, useEffect } from 'react';
const API_BASE_URL = 'http://localhost:5000/';

function App() {
  const [containers, setContainers] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [logs, setLogs] = useState('');
  const [loading, setLoading] = useState(false);
  const [sendLoading, setSendLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');
  const [stats, setStats] = useState(null);
  const [logFilter, setLogFilter] = useState('');

  const fetchContainers = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}containers`);
      if (!res.ok) {
        throw new Error(await res.text());
      }
      const data = await res.json();
      setContainers(data);
      setErrorMsg('');
    } catch (err) {
      setErrorMsg(err.message || 'Unknown error');
    }
  };

  const fetchStats = async (id) => {
    try {
      const res = await fetch(`${API_BASE_URL}containers/stats/${id}`);
      if (!res.ok) {
        throw new Error(await res.text());
      }
      const data = await res.json();
      setStats(data);
    } catch (err) {
      setErrorMsg(err.message || 'Unknown error');
    }
  };

  const createContainer = async () => {
    setLoading(true);
    try {
      const res = await fetch(`${API_BASE_URL}containers/create`, { method: 'POST' });
      if (!res.ok) {
        throw new Error(await res.text());
      }
      await fetchContainers();
      setErrorMsg('');
    } catch (err) {
      setErrorMsg(err.message || 'Unknown error');
    } finally {
      setLoading(false);
    }
  };

  const deleteContainer = async (id) => {
    setLoading(true);
    try {
      const res = await fetch(`${API_BASE_URL}containers/delete/${id}`, { method: 'DELETE' });
      if (!res.ok) {
        throw new Error(await res.text());
      }
      if (id === selectedId) {
        setSelectedId(null);
        setLogs('');
        setStats(null);
      }
      await fetchContainers();
      setErrorMsg('');
    } catch (err) {
      setErrorMsg(err.message || 'Unknown error');
    } finally {
      setLoading(false);
    }
  };

  const viewLogs = async (id, filter='') => {
    setSelectedId(id);
    setLogFilter(filter);
    try {
      const url = filter ? `${API_BASE_URL}containers/logs/${id}?filter=${filter}` : `${API_BASE_URL}containers/logs/${id}`;
      const res = await fetch(url);
      if (!res.ok) {
        throw new Error(await res.text());
      }
      const data = await res.json();
      setLogs(data.logs);
      setErrorMsg('');
    } catch (err) {
      setErrorMsg(err.message || 'Unknown error');
      setLogs('');
    }
    fetchStats(id);
  };

  const sendEmails = async (id) => {
    setSendLoading(true);
    try {
      const res = await fetch(`${API_BASE_URL}containers/send-emails/${id}`, { method: 'POST' });
      if (!res.ok) {
        throw new Error(await res.text());
      }
      const data = await res.json();
      alert(`Emails Sent: ${data.sent_in_batch}\nTotal Sent: ${data.total_sent}\nOutput:\n${data.output}`);
      await fetchStats(id);
      await viewLogs(id, logFilter);
      setErrorMsg('');
    } catch (err) {
      setErrorMsg(err.message || 'Unknown error');
    } finally {
      setSendLoading(false);
    }
  };

  useEffect(() => {
    fetchContainers();
  }, []);

  return (
    <div style={{ maxWidth: 1000, margin: 'auto', padding: 20 }}>
      <h1>SMTP Docker Dashboard</h1>

      <button onClick={createContainer} disabled={loading || sendLoading}>
        {loading ? 'Creating...' : 'Create New SMTP Container'}
      </button>

      {errorMsg && <p style={{ color: 'red' }}>{errorMsg}</p>}

      <h2>Containers</h2>
      <table border="1" cellPadding="5" style={{ width: '100%', marginBottom: 20 }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Status</th>
            <th>Base Email</th>
            <th>User Count</th>
            <th>Uptime (sec)</th>
            <th>Emails Sent</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {containers.length === 0 ? (
            <tr><td colSpan="7" style={{ textAlign: 'center' }}>No containers found.</td></tr>
          ) : (
            containers.map(c => (
              <tr key={c.id}>
                <td>{c.name}</td>
                <td>{c.status}</td>
                <td>{c.base_email}</td>
                <td>{c.user_count}</td>
                <td>{c.uptime_seconds}</td>
                <td>{c.emails_sent}</td>
                <td>
                  <button onClick={() => viewLogs(c.id)} disabled={loading || sendLoading}>View Logs</button>{' '}
                  <button onClick={() => sendEmails(c.id)} disabled={loading || sendLoading}>Send Emails</button>{' '}
                  <button onClick={() => deleteContainer(c.id)} disabled={loading || sendLoading}>Delete</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {selectedId && stats && (
        <div style={{ marginBottom: 20 }}>
          <h3>Container Stats for {selectedId}</h3>
          <p>CPU: {stats.cpu_percent.toFixed(2)}%</p>
          <p>Memory: {(stats.memory_usage / 1024 / 1024).toFixed(2)} MiB / {(stats.memory_limit / 1024 / 1024).toFixed(2)} MiB ({stats.memory_percent.toFixed(2)}%)</p>
          <p>Uptime: {stats.uptime_seconds} seconds</p>
          <p>Emails Sent: {stats.emails_sent}</p>
        </div>
      )}

      {selectedId && (
        <div style={{ whiteSpace: 'pre-wrap', background: '#eee', padding: 10, height: 350, overflowY: 'scroll', border: '1px solid #ccc' }}>
          <h3>Logs {logFilter ? `(filtered: ${logFilter})` : ''}</h3>
          <button onClick={() => viewLogs(selectedId, '')}>Show All</button>{' '}
          <button onClick={() => viewLogs(selectedId, 'error')}>Show Errors</button>
          <pre>{logs || 'No logs available.'}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
