import React, { useState, useEffect } from "react";
import MailEditor from "../components/MailEditor";
import MailList from "../components/MailList";
import axios from "axios";

export default function MailDesigner() {
  const [view, setView] = useState("create"); // 'create' or 'list'
  const [templates, setTemplates] = useState([]);
  const [selectedMail, setSelectedMail] = useState(null);

  useEffect(() => {
    if (view === "list") fetchTemplates();
  }, [view]);

  const fetchTemplates = async () => {
    try {
      const res = await axios.get("/api/mails");
      setTemplates(res.data);
    } catch (err) {
      console.error("Error fetching mails:", err);
    }
  };

  const handleEdit = (mail) => {
    setSelectedMail(mail);
    setView("create");
  };

  return (
    <div className="p-6">
      <div className="flex gap-4 mb-4">
        <button
          className={`px-4 py-2 rounded ${view === "create" ? "bg-blue-600 text-white" : "bg-gray-200"}`}
          onClick={() => { setView("create"); setSelectedMail(null); }}
        >
          Create New
        </button>
        <button
          className={`px-4 py-2 rounded ${view === "list" ? "bg-blue-600 text-white" : "bg-gray-200"}`}
          onClick={() => setView("list")}
        >
          View Saved
        </button>
      </div>

      {view === "create" ? (
        <MailEditor mailData={selectedMail} onSaveSuccess={() => setView("list")} />
      ) : (
        <MailList templates={templates} onEdit={handleEdit} />
      )}
    </div>
  );
}
