import React, { useRef, useState, useEffect } from "react";
import EmailEditor from "react-email-editor";
import axios from "axios";

export default function MailDesigner() {
  const emailEditorRef = useRef(null);
  const [templates, setTemplates] = useState([]);
  const [selectedTemplate, setSelectedTemplate] = useState("");
  const [loading, setLoading] = useState(false);

  // Load templates on mount
  useEffect(() => {
    axios.get("http://43.230.201.125:60025/mails")
      .then(res => setTemplates(res.data))
      .catch(console.error);
  }, []);

  const exportHtml = () => {
    emailEditorRef.current.editor.exportHtml(async (data) => {
      const { design, html } = data;
      const name = prompt("Enter template name:");
      if (!name) return alert("Template name required.");

      setLoading(true);
      try {
        await axios.post("http://43.230.201.125:60025/mails", { name, html, design });
        alert("‚úÖ Template saved successfully!");
      } catch (err) {
        console.error(err);
        alert("‚ùå Failed to save template");
      }
      setLoading(false);
    });
  };

  const loadTemplate = (template) => {
    setSelectedTemplate(template.id);
    emailEditorRef.current.editor.loadDesign(template.design);
  };

  const createNew = () => {
    setSelectedTemplate("");
    emailEditorRef.current.editor.loadDesign({});
  };

  const previewHtml = () => {
    emailEditorRef.current.editor.exportHtml((data) => {
      const previewWindow = window.open("", "_blank");
      previewWindow.document.write(data.html);
    });
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-semibold mb-6 text-center text-gray-800">
        ‚úâÔ∏è Email Template Builder
      </h1>

      <div className="flex items-center gap-4 mb-4 justify-center">
        <button
          onClick={createNew}
          className="px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-md"
        >
          üÜï New Template
        </button>

        <button
          onClick={exportHtml}
          disabled={loading}
          className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-md"
        >
          {loading ? "üíæ Saving..." : "üíæ Save Template"}
        </button>

        <button
          onClick={previewHtml}
          className="px-4 py-2 bg-purple-600 hover:bg-purple-700 text-white rounded-md"
        >
          üëÅÔ∏è Preview
        </button>

        <select
          onChange={(e) => {
            const template = templates.find(t => t.id === e.target.value);
            if (template) loadTemplate(template);
          }}
          value={selectedTemplate}
          className="px-3 py-2 border rounded-md bg-white"
        >
          <option value="">Load Template</option>
          {templates.map(t => (
            <option key={t.id} value={t.id}>{t.name}</option>
          ))}
        </select>
      </div>

      <div className="border rounded-lg overflow-hidden shadow-lg" style={{ height: "80vh" }}>
        <EmailEditor
          ref={emailEditorRef}
          onReady={() => console.log("‚úÖ Editor loaded")}
        />
      </div>
    </div>
  );
}
