import React, { useState, useEffect } from 'react';
const API_BASE_URL = 'http://localhost:5000/';

function App() {
  const [containers, setContainers] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [logs, setLogs] = useState('');
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');
  const [sendLoading, setSendLoading] = useState(false);

  // Fetch all containers
  const fetchContainers = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}containers`);
      if (!res.ok) {
        const text = await res.text();
        throw new Error(`Failed to fetch containers: ${text}`);
      }
      const data = await res.json();
      setContainers(data);
      setErrorMsg('');
    } catch (error) {
      setErrorMsg(error.message || 'Unknown error');
    }
  };

  // Create a new container
  const createContainer = async () => {
    setLoading(true);
    try {
      const res = await fetch(`${API_BASE_URL}containers/create`, { method: 'POST' });
      if (!res.ok) {
        const text = await res.text();
        throw new Error(`Failed to create container: ${text}`);
      }
      await fetchContainers();
    } catch (error) {
      setErrorMsg(error.message || 'Unknown error');
    } finally {
      setLoading(false);
    }
  };

  // Delete container
  const deleteContainer = async (id) => {
    setLoading(true);
    try {
      const res = await fetch(`${API_BASE_URL}containers/delete/${id}`, { method: 'DELETE' });
      if (!res.ok) {
        const text = await res.text();
        throw new Error(`Failed to delete container: ${text}`);
      }
      await fetchContainers();
      if (id === selectedId) {
        setSelectedId(null);
        setLogs('');
      }
    } catch (error) {
      setErrorMsg(error.message || 'Unknown error');
    } finally {
      setLoading(false);
    }
  };

  // View container logs
  const viewLogs = async (id) => {
    try {
      setSelectedId(id);
      const res = await fetch(`${API_BASE_URL}containers/logs/${id}`);
      if (!res.ok) {
        const text = await res.text();
        throw new Error(`Failed to load logs: ${text}`);
      }
      const data = await res.json();
      setLogs(data.logs);
    } catch (error) {
      setErrorMsg(error.message || 'Unknown error');
      setLogs('');
    }
  };

  // Send batch emails from container
  const sendEmails = async (id) => {
    setSendLoading(true);
    setErrorMsg('');
    try {
      const res = await fetch(`${API_BASE_URL}containers/send-emails/${id}`, { method: 'POST' });
      if (!res.ok) {
        const text = await res.text();
        throw new Error(`Failed to send emails: ${text}`);
      }
      const data = await res.json();
      alert(`Emails sent by container ${id}:\n${data.output}`);
      // Optionally refresh logs
      if(id === selectedId) viewLogs(id);
    } catch (error) {
      setErrorMsg(error.message || 'Unknown error');
    } finally {
      setSendLoading(false);
    }
  };

  useEffect(() => {
    fetchContainers();
  }, []);

  return (
    <div style={{ maxWidth: 900, margin: 'auto', padding: 32 }}>
      <h2>SMTP Docker Dashboard</h2>

      <button onClick={createContainer} disabled={loading || sendLoading}>
        {loading ? 'Processing...' : 'Create New SMTP Container'}
      </button>
      {errorMsg && <p style={{ color: 'red' }}>{errorMsg}</p>}

      <h3>Active Containers</h3>
      <table border="1" cellPadding="6" style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Status</th>
            <th>Base Email</th>
            <th>User Count</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {containers.length === 0 ? (
            <tr>
              <td colSpan="5" style={{ textAlign: 'center' }}>
                No containers found.
              </td>
            </tr>
          ) : (
            containers.map((cont) => (
              <tr key={cont.id}>
                <td>{cont.name}</td>
                <td>{cont.status}</td>
                <td>{cont.base_email}</td>
                <td>{cont.user_count}</td>
                <td>
                  <button onClick={() => viewLogs(cont.id)} disabled={loading || sendLoading}>
                    View Logs
                  </button>{' '}
                  <button onClick={() => sendEmails(cont.id)} disabled={loading || sendLoading}>
                    {sendLoading ? 'Sending...' : 'Send Emails'}
                  </button>{' '}
                  <button onClick={() => deleteContainer(cont.id)} disabled={loading || sendLoading}>
                    Delete
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      <h3>Container Logs</h3>
      {selectedId ? (
        <pre
          style={{
            whiteSpace: 'pre-wrap',
            background: '#eee',
            padding: 10,
            marginTop: 20,
            height: '300px',
            overflowY: 'auto',
          }}
        >
          {logs || 'No logs available.'}
        </pre>
      ) : (
        <p>Select a container to view logs.</p>
      )}
    </div>
  );
}

export default App;
