import React, { useEffect, useState, useRef } from "react";
import grapesjs from "grapesjs";
import "grapesjs/dist/css/grapes.min.css";
import "grapesjs-preset-newsletter";
import axios from "axios";
import {
  Layers,
  Settings,
  Square,
  Type,
  Image as ImageIcon,
  Columns,
  Save,
  Download,
} from "lucide-react";

const API_BASE = "http://43.230.201.125:60025/mails";

export default function MailDesigner() {
  const editorInstance = useRef(null);
  const [templates, setTemplates] = useState([]);
  const [selectedTemplate, setSelectedTemplate] = useState("");
  const [activeTab, setActiveTab] = useState("blocks");

  const fetchTemplates = async () => {
    try {
      const res = await axios.get(API_BASE);
      setTemplates(res.data);
    } catch (err) {
      console.error("Error loading templates:", err);
    }
  };

  useEffect(() => {
    const editor = grapesjs.init({
      container: "#gjs",
      height: "calc(100vh - 64px)",
      storageManager: false,
      plugins: ["gjs-preset-newsletter"],
      blockManager: { appendTo: "#blocks-panel" },
      layerManager: { appendTo: "#layers-panel" },
      styleManager: { appendTo: "#styles-panel" },
      traitManager: { appendTo: "#traits-panel" },
    });

    const bm = editor.BlockManager;
    bm.add("header", {
      label: `<div><Square size="12"/> Header</div>`,
      content: `<table style="width:100%;background:#f3f3f3;text-align:center;padding:20px;"><tr><td><h1>Welcome!</h1></td></tr></table>`,
      category: "Basic",
    });
    bm.add("text", {
      label: `<div><Type size="12"/> Text</div>`,
      content: `<div style="padding:10px;">Insert your text here...</div>`,
      category: "Basic",
    });
    bm.add("button", {
      label: `<div><Square size="12"/> Button</div>`,
      content: `<div style="text-align:center;padding:10px;"><a href="#" style="background:#007bff;color:white;padding:10px 20px;border-radius:4px;text-decoration:none;">Click Me</a></div>`,
      category: "Basic",
    });
    bm.add("image", {
      label: `<div><ImageIcon size="12"/> Image</div>`,
      content: `<img src="https://via.placeholder.com/600x200" style="width:100%;"/>`,
      category: "Media",
    });
    bm.add("two-columns", {
      label: `<div><Columns size="12"/> 2 Columns</div>`,
      content: `
        <table style="width:100%;">
          <tr>
            <td style="width:50%;padding:10px;">Left Content</td>
            <td style="width:50%;padding:10px;">Right Content</td>
          </tr>
        </table>`,
      category: "Layout",
    });

    editor.on("load", () => {
      editor.BlockManager.getCategories().each((cat) => cat.set("open", false));
    });

    editorInstance.current = editor;
    fetchTemplates();
    return () => editor.destroy();
  }, []);

  const handleSave = async () => {
    const editor = editorInstance.current;
    if (!editor) return;
    const name = prompt("Enter template name:");
    if (!name) return;
    const html = editor.getHtml();
    const css = editor.getCss();
    const design = editor.getComponents();

    try {
      await axios.post(API_BASE, { name, html, css, design });
      alert("✅ Template saved successfully!");
      fetchTemplates();
    } catch (err) {
      alert("❌ Failed to save template");
    }
  };

  const handleLoad = (id) => {
    const template = templates.find((t) => t.id === id);
    if (!template) return;
    const editor = editorInstance.current;
    editor.setComponents(template.design);
    editor.setStyle(template.css);
    setSelectedTemplate(id);
  };

  const handleExport = () => {
    const editor = editorInstance.current;
    const html = editor.getHtml();
    const css = editor.getCss();
    const fullHtml = `
      <html><head><style>${css}</style></head><body>${html}</body></html>`;
    const blob = new Blob([fullHtml], { type: "text/html" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "email_template.html";
    a.click();
  };

  const toolTabs = [
    { id: "blocks", icon: <Square size={20} />, label: "Blocks" },
    { id: "layers", icon: <Layers size={20} />, label: "Layers" },
    { id: "styles", icon: <Settings size={20} />, label: "Styles" },
    { id: "traits", icon: <Type size={20} />, label: "Traits" },
  ];

  return (
    <div className="mail-designer">
      {/* Top Bar */}
      <div className="designer-header">
        <h1 className="text-lg font-semibold tracking-wide text-indigo-700">
          ✉️ Mail Template Builder
        </h1>
        <div className="designer-actions">
          <select
            value={selectedTemplate}
            onChange={(e) => handleLoad(e.target.value)}
            className="designer-select"
          >
            <option value="">Load Template</option>
            {templates.map((t) => (
              <option key={t.id} value={t.id}>
                {t.name}
              </option>
            ))}
          </select>
          <button onClick={handleSave} className="btn-save">
            <Save size={16} /> Save
          </button>
          <button onClick={handleExport} className="btn-export">
            <Download size={16} /> Export
          </button>
        </div>
      </div>

      {/* Main Section */}
      <div className="editor-wrapper">
        {/* Sidebar */}
        <div className="sidebar">
          {toolTabs.map((tab) => (
            <button
              key={tab.id}
              title={tab.label}
              onClick={() => setActiveTab(tab.id)}
              className={`sidebar-btn ${
                activeTab === tab.id ? "active-tab" : ""
              }`}
            >
              {tab.icon}
              <span className="sidebar-label">{tab.label}</span>
            </button>
          ))}
          {/* Panels */}
          <div className="sidebar-section">
            {activeTab === "blocks" && <div id="blocks-panel"></div>}
            {activeTab === "layers" && <div id="layers-panel"></div>}
            {activeTab === "styles" && <div id="styles-panel"></div>}
            {activeTab === "traits" && <div id="traits-panel"></div>}
          </div>
        </div>

        {/* Editor Canvas */}
        <div className="editor-container">
          <div id="gjs" />
        </div>
      </div>
    </div>
  );
}
