import React, { useState, useEffect } from 'react';
import './App.css';

const API_BASE_URL = 'http://localhost:5000/';

function App() {
  const [containers, setContainers] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [logs, setLogs] = useState('');
  const [loading, setLoading] = useState(false);
  const [sendLoading, setSendLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');
  const [stats, setStats] = useState(null);
  const [logFilter, setLogFilter] = useState('');

  const [emailLogs, setEmailLogs] = useState([]);
  const [emailsLoading, setEmailsLoading] = useState(false);
  const [emailsError, setEmailsError] = useState('');

  const fetchContainers = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}containers`);
      if (!res.ok) throw new Error(await res.text());
      const data = await res.json();
      setContainers(data);
      setErrorMsg('');
    } catch (err) {
      setErrorMsg(err.message || 'Unknown error');
    }
  };

  const fetchStats = async (id) => {
    try {
      const res = await fetch(`${API_BASE_URL}containers/stats/${id}`);
      if (!res.ok) throw new Error(await res.text());
      const data = await res.json();
      setStats(data);
    } catch (err) {
      setErrorMsg(err.message || 'Unknown error');
    }
  };

  const fetchEmailLogs = async (containerId) => {
    setEmailsLoading(true);
    try {
      const res = await fetch(`${API_BASE_URL}containers/emails/${containerId}`);
      if (!res.ok) throw new Error(await res.text());
      const data = await res.json();
      setEmailLogs(data);
      setEmailsError('');
    } catch (err) {
      setEmailsError(err.message || 'Failed to load email logs');
      setEmailLogs([]);
    } finally {
      setEmailsLoading(false);
    }
  };

 const createContainer = async (hostDirPath) => {
  setLoading(true);
  try {
    const res = await fetch(`${API_BASE_URL}containers/create`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ host_dir: hostDirPath || '/default/path/email_logs' }), // pass actual host path here or default
    });
    if (!res.ok) throw new Error(await res.text());
    await fetchContainers();
    setErrorMsg('');
  } catch (err) {
    setErrorMsg(err.message || 'Unknown error');
  } finally {
    setLoading(false);
  }
};


  const deleteContainer = async (id) => {
    setLoading(true);
    try {
      const res = await fetch(`${API_BASE_URL}containers/delete/${id}`, { method: 'DELETE' });
      if (!res.ok) throw new Error(await res.text());
      if (id === selectedId) {
        setSelectedId(null);
        setLogs('');
        setStats(null);
        setEmailLogs([]);
      }
      await fetchContainers();
      setErrorMsg('');
    } catch (err) {
      setErrorMsg(err.message || 'Unknown error');
    } finally {
      setLoading(false);
    }
  };

  const viewLogs = async (id, filter = '') => {
    setSelectedId(id);
    setLogFilter(filter);
    try {
      const url = filter ? `${API_BASE_URL}containers/logs/${id}?filter=${filter}` : `${API_BASE_URL}containers/logs/${id}`;
      const res = await fetch(url);
      if (!res.ok) throw new Error(await res.text());
      const data = await res.json();
      setLogs(data.logs);
      setErrorMsg('');
    } catch (err) {
      setErrorMsg(err.message || 'Unknown error');
      setLogs('');
    }
    fetchStats(id);
    fetchEmailLogs(id);  // load email details along with logs
  };

  const sendEmails = async (id) => {
    setSendLoading(true);
    try {
      const res = await fetch(`${API_BASE_URL}containers/send-emails/${id}`, { method: 'POST' });
      if (!res.ok) throw new Error(await res.text());
      const data = await res.json();
      alert(`Emails Sent: ${data.sent_in_batch || 'N/A'}\nTotal Sent: ${data.total_sent || 'N/A'}\nOutput:\n${data.output}`);
      await fetchStats(id);
      await viewLogs(id, logFilter);
      setErrorMsg('');
    } catch (err) {
      setErrorMsg(err.message || 'Unknown error');
    } finally {
      setSendLoading(false);
    }
  };

  useEffect(() => {
    fetchContainers();
  }, []);

  return (
    <div className="dashboard-container">
      <h1>SMTP Docker Dashboard</h1>

      <button
        disabled={loading || sendLoading}
        onClick={createContainer}
        className="btn"
      >
        {loading ? 'Creating...' : 'Create New SMTP Container'}
      </button>

      {errorMsg && <p className="error-message">{errorMsg}</p>}

      <h2>Containers</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Status</th>
            <th>Base Email</th>
            <th>User Count</th>
            <th>Uptime (sec)</th>
            <th>Emails Sent</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {containers.length === 0 ? (
            <tr>
              <td colSpan="7" style={{ textAlign: 'center', padding: 20 }}>
                No containers found.
              </td>
            </tr>
          ) : (
            containers.map((c) => (
              <tr key={c.id}>
                <td>{c.name}</td>
                <td>{c.status}</td>
                <td>{c.base_email}</td>
                <td>{c.user_count}</td>
                <td>{c.uptime_seconds}</td>
                <td>{c.emails_sent}</td>
                <td>
                  <button
                    disabled={loading || sendLoading}
                    onClick={() => viewLogs(c.id)}
                    className="btn"
                  >
                    View Logs
                  </button>
                  <button
                    disabled={loading || sendLoading}
                    onClick={() => sendEmails(c.id)}
                    className="btn"
                  >
                    Send Emails
                  </button>
                  <button
                    disabled={loading || sendLoading}
                    onClick={() => deleteContainer(c.id)}
                    className="btn btn-delete"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {selectedId && stats && (
        <div className="stats-section">
          <h3>Container Stats for {selectedId}</h3>
          <p>
            <strong>CPU:</strong> {stats.cpu_percent.toFixed(2)}%
          </p>
          <p>
            <strong>Memory:</strong> {(stats.memory_usage / 1024 / 1024).toFixed(2)} MiB /{' '}
            {(stats.memory_limit / 1024 / 1024).toFixed(2)} MiB ({stats.memory_percent.toFixed(2)}%)
          </p>
          <p>
            <strong>Uptime:</strong> {stats.uptime_seconds} seconds
          </p>
          <p>
            <strong>Emails Sent:</strong> {stats.emails_sent}
          </p>
        </div>
      )}

      {selectedId && (
        <>
          <div className="log-panel">
            <h3>
              Logs {logFilter ? `(filtered: ${logFilter})` : ''}
              <button
                onClick={() => viewLogs(selectedId, '')}
                className="btn btn-small"
                style={{ marginLeft: 10 }}
              >
                Show All
              </button>{' '}
              <button
                onClick={() => viewLogs(selectedId, 'error')}
                className="btn btn-small"
                style={{ marginLeft: 5 }}
              >
                Show Errors
              </button>
            </h3>
            <pre>{logs || 'No logs available.'}</pre>
          </div>

          <div className="email-logs-section" style={{ marginTop: 30 }}>
            <h3>Email Logs</h3>
            {emailsLoading && <p>Loading emails...</p>}
            {emailsError && <p style={{ color: 'red' }}>{emailsError}</p>}
            {!emailsLoading && emailLogs.length === 0 && <p>No emails logged yet.</p>}
            {!emailsLoading && emailLogs.length > 0 && (
              <table className="table" style={{ marginTop: 10 }}>
                <thead>
                  <tr>
                    <th>Recipient</th>
                    <th>Subject</th>
                    <th>Status</th>
                    <th>Timestamp</th>
                    <th>Content Snippet</th>
                  </tr>
                </thead>
                <tbody>
                  {emailLogs.map((mail, idx) => (
                    <tr
                      key={idx}
                      style={{
                        backgroundColor:
                          mail.status === 'success' ? '#d4edda' : '#f8d7da',
                      }}
                    >
                      <td>{mail.to}</td>
                      <td>{mail.subject}</td>
                      <td>{mail.status}</td>
                      <td>{new Date(mail.timestamp * 1000).toLocaleString()}</td>
                      <td
                        style={{
                          whiteSpace: 'pre-wrap',
                          maxWidth: 400,
                          overflowWrap: 'break-word',
                        }}
                      >
                        {mail.body_snippet}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default App;
